import React from 'react';

const TextInput = ({ name, id, value, onChange }) => {
    return (
        <div className='mx-auto my-5 w-full max-w-md'>
            <label
                className='
                block 
                text-lg 
                font-medium
                text-gray-700 mb-1
                text-left
                '
                htmlFor={id}
            >
                Recipe Name:
            </label>
            <textarea
                className='
                resize
                border
                border-black
                rounded-md
                w-full
                '
                name={name}
                id={id}
                value={value}
                onChange={onChange}
            >
            </textarea>
        </div >
    );
};

export default TextInput;
