import React from 'react';

const UtiltiyButton = ({ text_color, bg_color, hover_color, onClick, text }) => {

    let buttonClass = `
    px4
    py-5
    rounded-md
    w-1/2 
    mr-2
    ${text_color} 
    ${bg_color} 
    ${hover_color}
    `;

    return (
        <button
            className={buttonClass}
            onClick={onClick}
        >
            {text}
        </button>
    );
};

export default UtiltiyButton;
