import React, { useState } from 'react';
import TextInput from './TextInput';
import UtiltiyButton from '../button/UtiltiyButton';
/* Parameters are: label, name, id, value, onChange, resize_or_not */

const Form = () => {

    const [currentRecipe, setCurrentRecipe] = useState({
        recipe_name: "",
        recipe_ingredients: "",
        recipe_instructions: "",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setCurrentRecipe((prevRecipe) => ({
            ...prevRecipe,
            [name]: value,
        }));
    };

    return (
        <div
            className='p-5 max-w-lg items-center'
        >
            <h2
                className='text-center text-3xl font-bold text-gray-700'
            >
                New Recipe
            </h2>
            <form>
                <TextInput
                    label="Recipe Name"
                    name="recipe_name_input"
                    id="recipe_name"
                    value={currentRecipe.recipe_name}
                    onChange={handleChange}
                    resize_or_not={false}
                />
                <TextInput
                    label="Ingredients"
                    name="recipe_ingredients_input"
                    id="recipe_ingredients"
                    value={currentRecipe.recipe_ingredients}
                    onChange={handleChange}
                    resize_or_not={true}
                />
                <TextInput
                    label="Directions"
                    name="recipe_directions_input"
                    id="recipe_directions"
                    value={currentRecipe.recipe_instructions}
                    onChange={handleChange}
                    resize_or_not={true}
                />
            </form>
        </div>
    );
};

export default Form;
