import React from 'react';

const TextInput = ({ name, id, value, onChange }) => {
    return (
        <div
            className='mx-5 my-5'
        >
            <label
                className='
                block 
                text-lg 
                font-medium
                text-gray-700 mb-1
                '
                htmlFor={id}
            >
                Recipe Name:
                {/* {name} */}
            </label>
            <textarea
                className='
                resize
                border
                border-black
                rounded-md
                max-w-md
                '
                name={name}
                id={id}
                value={value}
                onChange={onChange}
            // cols="30"
            // rows="10"
            >

            </textarea>
        </div >
    );
};

export default TextInput;
