import React from 'react';

const TextInput = ({ label, name, id, value, onChange, resize_or_not }) => {

    // Dynamically determine whether to resize the textarea or not
    let textAreaClass = "border border-black rounded-md w-full";
    if (resize_or_not) {
        textAreaClass += " resize";
    } else {
        textAreaClass += " resize-none";
    }

    return (
        <div
            className="
            mx-auto
            my-5
            w-full
            max-w-sm
            "
        >
            <label
                className="
                text-left
                block 
                text-lg 
                font-medium
                text-gray-700 mb-1
                "
                htmlFor={id}
            >
                {label}
            </label>
            <textarea
                className={textAreaClass}
                name={name}
                id={id}
                value={value}
                onChange={onChange}
            >
            </textarea>
        </div >
    );
};

export default TextInput;
