import React from 'react';

const UtiltiyButton = ({ text_color, bg_color, hover_color, handleClick, text }) => {

    let buttonClass = `px4 py-2 rounded-md ${text_color} ${bg_color} ${hover_color}`;

    return (
        <button
            className={buttonClass}
            onClick={handleClick}
        >
            {text}
        </button>
    );
};

export default UtiltiyButton;
